    val listData: ArrayList<Novel>
    get() {
        var list = ArrayList<Novel>()
        for(item in data) {
            val novel = Novel()

            novel.title         = item["title"].toString()
            novel.photo         = item["photo"] as Int
            novel.author        = item["author"].toString()
            novel.rates         = item["rates"].toString()
            novel.synopsis      = item["synopsis"].toString()

            list.add(novel)
        }
        return list
    }
}